import argparseimport osimport pandas as pddef main(ifilename, ofilename):    dfw = pd.read_csv(ifilename, sep="\t")    mapping = {5: 3, 4: 3, 3: 2, 2: 1, 1: 1}    dfw["label_AB"] = dfw["label_AB"].apply(lambda x: mapping[x])    dfw.to_csv(ofilename)if __name__ == "__main__":    parser = argparse.ArgumentParser()    parser.add_argument("-i", "--input-csv", help="path to 5 scale csv")    parser.add_argument("-o", "--output-csv", help="path to output 3 scale csv")    args = parser.parse_args()    main(os.path.expanduser(args.input_csv), os.path.expanduser(args.output_csv))